import React, { useState, useEffect } from 'react';
import CharacterList from './components/CharacterList';
import Modal from './components/Modal';
import './styles.css';

const characters = [
  { name: "Iron Man", description: "A wealthy American business magnate and a genius inventor.", image: "/images/ironman.jpg" },
  { name: "Spider-Man", description: "A young hero with spider-like abilities.", image: "/images/spiderman.jpg" },
  { name: "Captain America", description: "A super-soldier from World War II.", image: "/images/captainamerica.jpg" }
];

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const [isDarkTheme, setIsDarkTheme] = useState(false);

  const filteredCharacters = characters.filter(character =>
    character.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleCharacterClick = (character) => {
    setSelectedCharacter(character);
  };

  const handleCloseModal = () => {
    setSelectedCharacter(null);
  };

  const toggleTheme = () => {
    setIsDarkTheme(prevTheme => !prevTheme);
  };

  useEffect(() => {
    document.body.className = isDarkTheme ? 'dark-theme' : 'light-theme';
  }, [isDarkTheme]);

  return (
    <div className="App">
      <header>
        <h1>Marvel Library</h1>
        <input type="text" placeholder="Search for a character..." value={searchTerm} onChange={handleSearch} />
        <button onClick={toggleTheme}>Toggle Theme</button>
      </header>
      <CharacterList characters={filteredCharacters} onCharacterClick={handleCharacterClick} />
      {selectedCharacter && <Modal character={selectedCharacter} onClose={handleCloseModal} />}
      <footer>
        <p>&copy; 2024 Marvel Library</p>
      </footer>
    </div>
  );
};

export default App;
